from models.tile import TileModelfrom .analysis import n_tet_countsclass SimpleRater:    def rate(self, board):        COUPLE_COST = 5        TRIPLET_COST = 10        QUARTET_COST = 20        WIN_COST = 99999        couple_counts = n_tet_counts(board, 2)        triplet_counts = n_tet_counts(board, 3)        quartet_counts = n_tet_counts(board, 4)        win_counts = n_tet_counts(board, 5)        return (couple_counts[0] - couple_counts[1]) * COUPLE_COST + \               (triplet_counts[0] - triplet_counts[1]) * TRIPLET_COST + \               (quartet_counts[0] - quartet_counts[1]) * QUARTET_COST + \               (win_counts[0] - win_counts[1]) * WIN_COST